from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left_wheel = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right_wheel = Motor(Port.B, Direction.CLOCKWISE)
robot = DriveBase(left_wheel, right_wheel, 88, 97)
left_motor = Motor(Port.E, Direction.CLOCKWISE)
right_motor = Motor(Port.F, Direction.CLOCKWISE)

def mediumSpeed():
    robot.use_gyro(True)
    robot.settings(straight_speed=400)
    robot.settings(straight_acceleration=150)
    robot.settings(turn_rate=150)
    robot.settings(turn_acceleration=750)

def slowTurn():
    robot.use_gyro(True)
    robot.settings(turn_rate=150)
    robot.settings(turn_acceleration=200)


# The main program starts here.
mediumSpeed()
robot.straight(475)
right_motor.run_angle(500, 650)
robot.straight(480)
robot.turn(30)
right_motor.run_angle(500, -650)
robot.turn(-30)
robot.straight(-25)
robot.curve(-100, 80)
robot.curve(-100, -80)
robot.turn(15)
robot.straight(400)
robot.curve(-300, -50)
robot.curve(-400, 140)
