from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.B, Direction.CLOCKWISE)
robot = DriveBase(left, right, 88, 97)
left_motor = Motor(Port.E, Direction.CLOCKWISE)
right_motor = Motor(Port.F, Direction.CLOCKWISE)

def fastSpeed():
    robot.use_gyro(True)
    robot.settings(straight_speed=400)
    robot.settings(straight_acceleration=1000)

def mediumSpeed():
    robot.use_gyro(True)
    robot.settings(straight_speed=1000)
    robot.settings(straight_acceleration=200)

def turnSpeed():
    robot.settings(turn_rate=1000)
    robot.settings(turn_acceleration=100)

def slowSpeed():
    robot.use_gyro(True)
    robot.settings(straight_speed=500)
    robot.settings(straight_acceleration=100)


# The main program starts here.
mediumSpeed()
turnSpeed()
robot.straight(-680)
robot.turn(45)
slowSpeed()
robot.straight(-280)
mediumSpeed()
robot.straight(180)
robot.turn(-93)
slowSpeed()
robot.straight(-160)
left_motor.run_angle(500, -720)
robot.straight(160)
mediumSpeed()
left_motor.run_angle(500, 720)
robot.turn(47)
robot.straight(680)
